mapper.select:
SELECT pd.id, pd.name, pd.address, pd.description, pd.category AS category_id, ca.name AS category_name, ca.display_name AS category_display_name, ca.color AS category_color, ca.icon AS category_icon, pd.longitude, pd.latitude, pd.creator, pd.created_time, pd.modified_time, pd.photo FROM poi_data AS pd LEFT JOIN category AS ca ON pd.category = ca.id WHERE
( pd.id LIKE concat('%', #{searchModel.keywords}, '%') OR pd.name LIKE concat('%', #{searchModel.keywords}, '%') OR pd.address LIKE concat('%', #{searchModel.keywords}, '%') )
pd.category = #{searchModel.categoryId}
